package com.dilipsuthar.mvvmrecyclerview;

import android.arch.lifecycle.Observer;
import android.arch.lifecycle.ViewModelProviders;
import android.support.annotation.Nullable;
import android.support.design.widget.FloatingActionButton;
import android.support.v7.app.AppCompatActivity;
import android.os.Bundle;
import android.support.v7.widget.LinearLayoutManager;
import android.support.v7.widget.RecyclerView;
import android.view.View;
import android.widget.ProgressBar;

import com.dilipsuthar.mvvmrecyclerview.adapters.RecyclerAdapter;
import com.dilipsuthar.mvvmrecyclerview.models.NicePlace;
import com.dilipsuthar.mvvmrecyclerview.viewmodels.NicePlaceViewModel;

import java.util.List;
import java.util.Objects;

public class MainActivity extends AppCompatActivity {

    private static final String TAG = "MainActivity";

    private RecyclerView mRecyclerView;
    private RecyclerAdapter mRecyclerAdapter;
    private FloatingActionButton mFab;
    private ProgressBar mProgressBar;
    private NicePlaceViewModel mNicePlaceViewModel;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);
        mFab = findViewById(R.id.fab);
        mRecyclerView = findViewById(R.id.recycler_view);
        mProgressBar = findViewById(R.id.progress_bar);

        // MVVM code
        mNicePlaceViewModel = ViewModelProviders.of(this).get(NicePlaceViewModel.class);
        mNicePlaceViewModel.init();
        mNicePlaceViewModel.getNicePlaces().observe(this, new Observer<List<NicePlace>>() {
            @Override
            public void onChanged(@Nullable List<NicePlace> nicePlaces) {
                mRecyclerAdapter.notifyDataSetChanged();
            }
        });

        mNicePlaceViewModel.getIsUpdating().observe(this, new Observer<Boolean>() {
            @Override
            public void onChanged(@Nullable Boolean aBoolean) {
                if (aBoolean != null) {
                    if (aBoolean) showProgressBar();
                    else {
                        hideProgressBar();
                        mRecyclerView.smoothScrollToPosition(Objects.requireNonNull(mNicePlaceViewModel.getNicePlaces().getValue()).size() - 1);
                    }
                }
            }
        });

        mFab.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View v) {
                mNicePlaceViewModel.addNewValue(
                        new NicePlace("data:image/jpeg;base64,\"",
                                "Havasu Falls")
                );
            }
        });

        initRecyclerView();


    }

    private void initRecyclerView() {
        mRecyclerAdapter = new RecyclerAdapter(this, mNicePlaceViewModel.getNicePlaces().getValue());
        mRecyclerView.setLayoutManager(new LinearLayoutManager(this));
        mRecyclerView.setAdapter(mRecyclerAdapter);
    }

    private void showProgressBar() {
        mProgressBar.setVisibility(View.VISIBLE);
    }

    private void hideProgressBar() {
        mProgressBar.setVisibility(View.GONE);
    }

}
